package introtojava;

import java.util.Scanner;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int numberOne = 20;
int numberTwo = 40;
int multiply = numberOne * numberTwo; 
int addition = numberOne + numberTwo;
double division = numberTwo / numberOne;
int subtraction = numberTwo - numberOne;
System.out.println("This is the result for multiplication = "+multiply);
System.out.println("This is the result for addition = " + addition);
System.out.println("This is the result for subtraction = " + subtraction);
System.out.println("This is the result for division = " + division);


		
	    
	}

}
